// api.js
const DEPLOYED_URL = "https://studentapp-4.onrender.com";
const LOCAL_URL = "http://127.0.0.1:8000";

export const API_URL = async () => {
  try {
    // Try fetching a simple endpoint from deployed backend
    const res = await fetch(`${DEPLOYED_URL}/students`, { method: "HEAD" });
    if (res.ok) {
      return DEPLOYED_URL;
    }
    throw new Error("Deployed backend not reachable");
  } catch (err) {
    console.warn("Falling back to localhost:", err.message);
    return LOCAL_URL;
  }
};
