useEffect(() => {
  const fetchData = async () => {
    setLoading(true);
    try {
      const resStudents = await fetch(`${API_URL}/students`);
      if (!resStudents.ok) throw new Error("Failed to fetch students");
      const studentsData = await resStudents.json();
      setStudents(studentsData);

      const resCheckins = await fetch(`${API_URL}/checkins`);
      if (!resCheckins.ok) throw new Error("Failed to fetch check-ins");
      const checkinsData = await resCheckins.json();
      setCheckins(checkinsData);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  fetchData();
}, []);
