import axios from "axios";

const API_URL = "https://studentapp-4.onrender.com"; 

export const fetchStudents = async () => {
  return await axios.get(`${API_URL}/students`);
};

export const addStudent = async (student) => {
  return await axios.post(`${API_URL}/students`, student);
};

export const checkInStudent = async (studentId) => {
  return await axios.post(`${API_URL}/checkin`, { student_id: studentId });
};

export const fetchCheckIns = async () => {
  return await axios.get(`${API_URL}/checkins`);
};
