import React, { useEffect, useState } from "react";
import StudentForm from "./components/StudentForm";
import StudentTable from "./components/StudentTable";
import CheckInForm from "./components/CheckInForm";
import CheckInTable from "./components/CheckInTable";

const DEPLOYED_URL = "https://studentapp-4.onrender.com";
const LOCAL_URL = "http://127.0.0.1:8000";

// Helper function to choose API URL
const getApiUrl = async () => {
  try {
    const res = await fetch(`${DEPLOYED_URL}/students`, { method: "HEAD" });
    if (res.ok) return DEPLOYED_URL;
    throw new Error("Deployed backend not reachable");
  } catch (err) {
    console.warn("Falling back to localhost:", err.message);
    return LOCAL_URL;
  }
};

function App() {
  const [students, setStudents] = useState([]);
  const [checkins, setCheckins] = useState([]);
  const [loading, setLoading] = useState(true);
  const [apiUrl, setApiUrl] = useState(DEPLOYED_URL);

  useEffect(() => {
    const initialize = async () => {
      const baseUrl = await getApiUrl();
      setApiUrl(baseUrl);

      try {
        const res = await fetch(`${baseUrl}/students`);
        const data = await res.json();
        setStudents(data);

        const resCheckins = await fetch(`${baseUrl}/checkins`);
        const checkinData = await resCheckins.json();
        setCheckins(checkinData);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    initialize();
  }, []);

  const addStudent = (student) => setStudents([...students, student]);
  const addCheckIn = (checkin) => setCheckins([...checkins, checkin]);

  if (loading) return <p>Loading...</p>;

  return (
    <div className="container">
      <h1>Student Dashboard</h1>
      <div className="section">
        <StudentForm onAdd={addStudent} apiUrl={apiUrl} />
        <StudentTable students={students} />
      </div>
      <div className="section">
        <CheckInForm onCheckIn={addCheckIn} apiUrl={apiUrl} />
        <CheckInTable checkins={checkins} />
      </div>
    </div>
  );
}

export default App;
